package week1.day2;

public class LearnForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=10;
		for(int i=0;i<=n;i++)
		{
			System.out.println(i);
		}
				
       for (int j=0;j<=10;j++)
       {
    	   if (j%2==0)
   			System.out.println(j+" is Even Number");
   		else 
   			System.out.println(j+" is ODD Number");
   	  
       }
	}

}
